def dicmaker(file):
    newdic = {}
    file = open(file, 'r')
    for line in file.readlines():
        if len(line) > 1:
            key, item = line.split(',')[0], line.split(',')[1]
            print(key, item)
            newdic[key] = item
    return newdic

#dyftongi = dicmaker('dyft')

def transcriber(word):
    podstawowy.update(przydechy)

    witem = ''
    word = word.lower()
    for num in range(len(word)):
        dyf = word[num:num + 2]
        let = word[num:num + 1]
        if dyf in dyftongi.keys():
            witem += str(dyftongi[dyf])

        elif word[num - 1:num + 1] in dyftongi.keys():
            pass

        elif let in podstawowy.keys():
            witem += str(podstawowy[let])

    return witem
numbers = [str(i) for i in range(10)]

def onlywordslister(text, minlength=2):
    words = []
    file = open(text, 'r')
    for line in file.readlines():
        for word in line.split(' '):
            for j in numbers:
                if j in word:

                    word = word.replace(j, '')
            word = ''.join(word.split())
            words.append(word)



    onlywords = set(words)
    onlywordslist = list(onlywords)
    for word in onlywordslist:
        if len(word) < minlength:
            onlywordslist.remove(word)

    return onlywordslist

lista = onlywordslister('genesis.txt')



newdict = {}

for word in lista:

    key = word
    item = transcriber(word)
    newdict[key] = item

# dictionary of transcribed words
print(newdict)
